mapper.select:
SELECT 
FROM config_item
WHERE  group_name = #{groupName}
  AND config_key = #{configKey}
  AND del_flag = 0

SELECT 
FROM config_item
WHERE del_flag = 0

    AND config_key = #{configKey}


    AND group_name = #{groupName}


    AND status = #{status}

ORDER BY update_time DESC
LIMIT #{pageSize} OFFSET #{pageNum}

SELECT COUNT(1)
FROM config_item
WHERE del_flag = 0

    AND config_key = #{configKey}


    AND group_name = #{groupName}


    AND status = #{status}

mapper.insert:
INSERT INTO config_item (
    group_name, config_key, config_value, data_type,
    description, version, encrypted, tags, status, zk_path, last_publish_time,
    publisher, create_time, update_time, create_by, update_by, remark, del_flag
) VALUES (
    #{groupName}, #{configKey}, #{configValue}, #{dataType},
    #{description}, #{version}, #{encrypted}, #{tags}, #{status}, #{zkPath}, #{lastPublishTime},
    #{publisher}, #{createTime}, #{updateTime}, #{createBy}, #{updateBy}, #{remark}, #{delFlag}
)

mapper.update:
UPDATE config_item
SET config_value = #{configValue}
WHERE id = #{id}
